export function uniq(xs) {
  return Array.from(new Set(xs));
}

export function match(numb, guess) {

  let numbers = numb.split('');
  let goods = guess.split('').filter((x) => numbers.includes(x));
  let bulls = 0;
  let cows = goods.length;

  for (const x in goods) {
    if (numb.indexOf(x) == guess.indexOf(x)) {
      cows -= 1;
      bulls += 1;
    }
  }
  return (bulls + "B" + cows + "C");
}

export function test_guess(text) {
  if (text.length == 4) {
    let isUnique = true;
    let copy = text.slice();
    let i, j;
    for (i = 0; i < 4; i++) {
      for (j = i + 1; j < 4; j++) {
        if (copy[i] === text[j]) {
          isUnique = false;
        }
      }
    }
    if (isUnique) {
      return true;
    }
    else {
      alert("Your four-digit guess must have four unique digits!");
      return false;
    }
  }
  else {
    alert("Your guess must be a four-digit number!");
    return false;
  }
}

export function make_secret_numb() {
  let one = Math.floor(Math.random() * 10);
  let two = Math.floor(Math.random() * 10);
  if (two >= one) { two += 1 };
  let three = Math.floor(Math.random() * 10);
  if (three >= one) { three += 1 };
  if (three >= two) { three += 1 };
  let four = Math.floor(Math.random() * 10);
  if (four >= one) { four += 1 };
  if (four >= two) { four += 1 };
  if (four >= three) { four += 1 };
  return (one + two + three + four).toString();
}
