export function uniq(xs) {
  return Array.from(new Set(xs));
}

export function match(numb, guess) {

  let numbers = numb.split('');
  let goods = guess.filter((x) => numbers.includes(x));
  let bulls = 0;
  let cows = goods.length;

  for (const x in goods) {
    if (numb.indexOf(x) == guess.indexOf(x)) {
      cows -= 1;
      bulls += 1;
    }
  }

  let res = (bulls + "B" + cows.toString() + "C");
  return res;
}
