import { useState } from 'react';
import 'mini.css';
import { uniq, match, test_guess, make_secret_numb} from './game';
import './App.css';


function GameLost(r) {
  let { reset } = r;
  return (
    <div className="App">
      <h1>You Lost!</h1>
      <h3>Try again?</h3>
      <p>
        <button onClick={reset}>
          New Game
        </button>
      </p>
    </div>
  );
}

function GameWon(r) {
  let { reset } = r;
  return (
    <div className="App">
      <h1>You Won!</h1>
      <h3>Play again?</h3>
      <p>
        <button onClick={reset}>
          New Game
        </button>
      </p>
    </div>
  );
}

function App() {

  const [text, setText] = useState("");
  const [guesses, setGuesses] = useState([]);
  const [results, setResults] = useState([]);
  const [numb, setNumb] = useState(make_secret_numb());

  let turns = 8 - guesses.length;

  function updateText(ev) {
    let vv = ev.target.value;
    let cc = vv[vv.length - 1];
    if (!isNaN(cc) || vv === "") {
      setText(vv);
    }
  }

  function guess() {
    if (test_guess(text)) {
      let ng = uniq(guesses.concat(text));
      setGuesses(ng);
      let res = match(numb, text);
      setResults([...results, res]);
      setText("");
    }
  }

  function keyPress(ev) {
    if (ev.key === "Enter") {
      guess();
    }
  }

  function newGame() {
    setText("");
    setGuesses([]);
    setResults([]);
    setNumb(make_secret_numb());
  }

  if (turns <= 0) {
    return <GameLost reset={newGame} />;
  }

  if (results[results.length - 1] == "4B0C") {
    return <GameWon reset={(newGame)} />;
  }

  return (
    <div className="App">
      <h1>Bulls and Cows</h1>
      <p>Guess the four-digit number. More information on the game&nbsp;
       <a href="https://en.wikipedia.org/wiki/Bulls_and_Cows" rel="noreferrer" target="_blank">here</a>
        .
      </p>
      <p>
        Input:&nbsp;
        <input type="text"
          value={text}
          onChange={updateText}
          onKeyPress={keyPress} />
        <button onClick={guess}>OK</button>
      </p>
      <div class="container">
        <div class="row">
          <div class="col-sm-2"></div>
          <div class="col-sm-5">Guess</div>
          <div class="col-sm-5">Result</div>
        </div>
        <div class="row">
          <div class="col-sm-2">1</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">2</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">3</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">4</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">1</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">1</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">1</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
        <div class="row">
          <div class="col-sm-2">1</div>
          <div class="col-sm-5">{guesses[0]}</div>
          <div class="col-sm-5">{results[0]}</div>
        </div>
      </div>
      <button onClick={newGame}>New Game</button>
    </div>
  );
}

export default App;
