import { useState } from 'react';
import { uniq, match } from './game';
import './App.css';


function App() {

  const [text, setText] = useState("");
  const [guesses, setGuesses] = useState([]);
  const [results, setResults] = useState([]);
  const [numb, _setNumb] = useState("0123");

  function updateText(ev) {
    let vv = ev.target.value;
    let cc = vv[vv.length - 1];
    if (!isNaN(cc) || vv === "") {
      setText(vv);
    }
  }

  function guess() {
    if (text.length == 4) {
      let isUnique = true;
      let copy = text.slice();
      for (i = 0; i < 4; i++) {
        for (j = i; j < 4; j++) {
          if (copy[i] === text[j]) {
            isUnique = false;
          }
        }
      }
      if (isUnique) {
        let ng = uniq(guesses.concat(text));
        setGuesses(ng);
        let res = match(numb, text);
        setResults([...results, res]);
        setText("");
      }
      else {
        alert("Your four-digit number must be unique!");
      }
    }
    else {
      alert("")
    }

  }

  function keyPress(ev) {
    if (ev.key === "Enter") {
      guess();
    }
  }

  return (
    <div className="App">
      <h1>Bulls and Cows</h1>
      <p>Guess the four digit number. More information on the game&nbsp;
       <a href="https://en.wikipedia.org/wiki/Bulls_and_Cows" rel="noreferrer" target="_blank">here</a>
        .
      </p>
      <p>
        Input:&nbsp;
        <input type="text"
          value={text}
          onChange={updateText}
          onKeyPress={keyPress} />
        <button onClick={guess}>OK</button>
      </p>
      <table>
        <tr>
          <td></td>
          <th>Guess</th>
          <th>Result</th>
        </tr>
        <tr>
          <td>1</td>
          <td>{guesses[0]}</td>
          <td>{results[0]}</td>
        </tr>
        <tr>
          <td>2</td>
          <td>{guesses[1]}</td>
          <td>{results[1]}</td>
        </tr>
        <tr>
          <td>3</td>
          <td>{guesses[2]}</td>
          <td>{results[2]}</td>
        </tr>
        <tr>
          <td>4</td>
          <td>{guesses[3]}</td>
          <td>{results[3]}</td>
        </tr>
        <tr>
          <td>5</td>
          <td>{guesses[4]}</td>
          <td>{results[4]}</td>
        </tr>
        <tr>
          <td>6</td>
          <td>{guesses[5]}</td>
          <td>{results[5]}</td>
        </tr>
        <tr>
          <td>7</td>
          <td>{guesses[6]}</td>
          <td>{results[6]}</td>
        </tr>
        <tr>
          <td>8</td>
          <td>{guesses[7]}</td>
          <td>{results[7]}</td>
        </tr>
      </table>
    </div>
  );
}

export default App;
