import { useState } from 'react';
import { uniq } from './game';
import './App.css';


function App() {

  const [text, setText] = useState("");
  const [guesses, setGuesses] = useState([]);

  function updateText(ev) {
    let vv = ev.target.value;
    let cc = vv[vv.length - 1];
    if (!isNaN(cc)) {
      setText(vv);
    }e
  }

  function guess() {
    let ng = uniq(guesses.concat(text));
    console.log("ng", ng);
    setGuesses(ng);
    setText("");
  }

  function keyPress(ev) {
    if (ev.key == "Enter") {
      guess();
    }
  }

  return (
    <div className="App">
      <p>
        Input:
        <input type="text"
          value={text}
          onChange={updateText}
          onKeyPress={keyPress} />
        <button onClick={guess}>OK</button>
      </p>
      <table>
        <tr>
          <td></td>
          <th>Guess</th>
          <th>Result</th>
        </tr>
        <tr>
          <td>1</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>2</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>3</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>4</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>5</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>6</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>7</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>8</td>
          <td></td>
          <td></td>
        </tr>
      </table>
    </div>
  );
}

export default App;
